import React, { useState } from "react";
import "./Foodcard.css"; // create a CSS file for styling
import FoodModal2 from "./FoodModal2.jsx";
const FoodCard = ({ image, foodName, donorName, contact, location, quantity, quality, foodType, condition }) => {
  const [isModalOpen, setIsModalOpen] = useState(false);

  const arrayBufferToBase64 = (buffer) => {
    let binary = "";
    const bytes = new Uint8Array(buffer);
    for (let i = 0; i < bytes.length; i++) {
      binary += String.fromCharCode(bytes[i]);
    }
    return window.btoa(binary);
  };

  const imageSrc = `data:image/png;base64,${arrayBufferToBase64(image.data)}`;

  const openModal = () => {
    setIsModalOpen(true);
  };
  const closeModal = () => {
    setIsModalOpen(false);
  };


  return (
    <div className="food-card" >
      <img src={imageSrc} alt={foodName} />
      <div className="card-details">
        <h3>{foodName}</h3>
        <p>Donor: {donorName}</p>
        <p>Contact: {contact}</p>
        <p>Location: {location}</p>
        <button className="car-btn" onClick={openModal}>View More</button>
        {isModalOpen && (
          <FoodModal2
            onClose={closeModal}
            {...{ image, foodName, donorName, contact, location, quantity, quality, foodType, condition }}
          />
        )}
      </div>
    </div>
  );
};

export default FoodCard;
