// FoodCard.js

import React, { useState } from "react";
import "./Foodcard.css"; // create a CSS file for styling

const FoodCard = ({ image, foodName, donorName, contact, location, quantity, quality, foodType, condition }) => {
  const [isExpanded, setIsExpanded] = useState(false);

  const toggleDetails = () => {
    setIsExpanded(!isExpanded);
  };

  return (
    <div className="food-card">
      <img src={image} alt={foodName} />
      <div className="card-details">
        <h3>{foodName}</h3>
        <p>Donor: {donorName}</p>
        <p>Contact: {contact}</p>
        <p>Location: {location}</p>
        <button className="car-btn" onClick={toggleDetails}>{isExpanded ? "Hide Details" : "View More"}</button>
        {isExpanded && (
          <div className="expanded-details">
            <p>Quantity: {quantity}</p>
            <p>Quality: {quality}</p>
            <p>Food Type: {foodType}</p>
            <p>Condition: {condition}</p>
          </div>
        )}
      </div>
    </div>
  );
};

export default FoodCard;
