// FoodModal.js
// FoodModal.js
import React, { useState } from "react";
import "./FoodModal.css"; // create a CSS file for styling
import { useNavigate } from "react-router-dom";
import axios from "axios";

const FoodModal2 = ({ image, foodName, donorName, contact, location, quantity, email, foodType, condition, onClose }) => {
  const [isFullSize, setIsFullSize] = useState(false);
const toggleFullSize = () => {
  setIsFullSize(!isFullSize);
};
const navigate=useNavigate();
const handleDelete=(foodName)=>{
  axios.delete('http://localhost:8081/'+foodName)
  .then(res => navigate('/Add'))
  .catch(err => console.log(err));
}


  return (
    <div className="food-modal">
      <div className={`modal-content ${isFullSize ? "full-size" : ""}`} onClick={(e) => e.stopPropagation()}>
        <img src={image} alt={foodName} onClick={toggleFullSize} />
        <h3>{foodName}</h3>
        <p>Donor: {donorName}</p>
        <p>Contact: {contact}</p>
        <p>Location: {location}</p>
        <p>Quantity: {quantity}</p>
        <p>Email: {email}</p>
        <p>Food Type: {foodType}</p>
        <p>Condition: {condition}</p>
        <button className="del-btn">Delete</button>
        <button onClick={e=>handleDelete(foodName)} className="cls-btn" onClick={onClose}>Close</button>
      </div>
    </div>
  );
};

export default FoodModal2;
