import React, { useState } from "react";
import "./FoodModal.css"; // create a CSS file for styling

const FoodModal2 = ({ blobData, foodName, donorName, contact, location, quantity, quality, foodType, condition, onClose }) => {
  const [isFullSize, setIsFullSize] = useState(false);
const toggleFullSize = () => {
  setIsFullSize(!isFullSize);
};
const [image, setImageSrc] = useState(null);
useEffect(() => {
  // Convert blob data to Data URL
  const reader = new FileReader();
  reader.onload = () => {
    setImageSrc(reader.result);
  };
  reader.readAsDataURL(blobData);
}, [blobData]);


