import React from "react";
import "./Login.css";
import Validation from './LoginValidation.jsx';
import { useState } from "react";
import MenuIcon from '@mui/icons-material/Menu';
import {Link} from 'react-router-dom';
function Login() {
    const[values,setValues]=useState({
        email:'',
        password:''
    })
    const [errors,setErrors]=useState({})
    const handleSubmit=(event)=>{
        event.preventDefault();
        setErrors(Validation(values));
    }
    const handleInput=(event)=>{
        setValues(prev=>({...prev,[event.target.name]: [event.target.value]}))
    }







    const [isMenuOpen, setIsMenuOpen] = useState(false);
    const toggleMenu = () => {
        setIsMenuOpen(!isMenuOpen);
      };
    const year = new Date().getFullYear();
    return (
      <div>
        <div className="home-container">
            <div className="menu">
                <button className="menu-btn" onClick={toggleMenu}>
                    <MenuIcon/>
                </button>
                {isMenuOpen && (
                    <div className="dropdown">
                    <button className="menu-item">Contact</button>
                    <button className="menu-item">About Us</button>
                    </div>
                )}
            </div>
            <div className="tit">
                <h2><img src="/images/f3.png" alt=""/>FOOD NETWORK</h2>
            </div>
            <div className="buttons-container">
                <button className="log-btn">Login</button>
                <button className="sig-btn">Sign In</button>
            </div>
        </div>
        <div className="sig-area">
            <h3>Login</h3>
            <form action="" onSubmit={handleSubmit}>
                <div>
                    <input type="email" placeholder="Email" name='email' onChange={handleInput}/>
                    {errors.email && <span className="text-danger">{errors.email}</span>}
                </div>
                <div>
                    <input type="password" placeholder="Password" name='password' onChange={handleInput}/>
                    {errors.password && <span className="text-danger">{errors.password}</span>}
                </div>
                <Link to="/signup" className="para">New User? Create an account</Link>
                <div>
                    <button type="submit">Login</button>
                </div>
            </form>
        </div>




        <div className="footer">
            <h3>Copyright ⓒ {year}</h3>
        </div>
      </div>
    );
  }
  
  export default Login;
