// FoodCard.js

import React, { useState } from "react";
import "./Foodcard.css"; // create a CSS file for styling
import "./FoodModel.jsx";
const FoodCard = ({ image, foodName, donorName, contact, location, quantity, quality, foodType, condition }) => {
  const [isModalOpen, setIsModalOpen] = useState(false);

  const openModal = () => {
    setIsModalOpen(true);
  };
  const closeModal = () => {
    setIsModalOpen(false);
  };

  return (
    <div className="food-card" >
      <img src={image} alt={foodName} />
      <div className="card-details">
        <h3>{foodName}</h3>
        <p>Donor: {donorName}</p>
        <p>Contact: {contact}</p>
        <p>Location: {location}</p>
        <button className="car-btn" onClick={openModal}></button>
        {isModalOpen && (
          <FoodModal
          image={image}
          foodName={foodName}
          donorName={donorName}
          contact={contact}
          location={location}
          quantity={quantity}
          quality={quality}
          foodType={foodType}
          condition={condition}
          onClose={closeModal}
        />
        )}
      </div>
    </div>
  );
};

export default FoodCard;
