import React from "react";
import "./Add.css";
import FoodCard2 from "./Foodcard2.jsx";
import { useState,useEffect } from "react";
import axios from "axios";
import MenuIcon from '@mui/icons-material/Menu';
import { useNavigate } from "react-router-dom";
function Add() {
    const [isMenuOpen, setIsMenuOpen] = useState(false);
    const toggleMenu = () => {
        setIsMenuOpen(!isMenuOpen);
      };
    const year = new Date().getFullYear();
    


    useEffect(() => {
        axios.get('http://localhost:8081/Add')
            .then(res => {
                if (res.data.valid) {
                    console.log('success');
                } else {
                    navigate('/login');
                }
            })
            .catch(err => console.log(err));
    });


    const navigate=useNavigate();
  
    axios.defaults.withCredentials=true;

      const handleLogout = () => {
        axios.get('http://localhost:8081/logout')
            .then(() => {
                navigate('/login');
            })
            .catch(err => console.log(err));
    };


    const handleReload = () => {
        // Use navigate to reload the current page
        
        navigate('/', { replace: true });
        window.location.reload();
      };





    const [image, setimage] = useState('')
    const [donor, setdonor] = useState('')
    const [foodname, setfoodname] = useState('')
    const [quality, setquality] = useState('')
    const [quantity, setquantity] = useState('')
    const [condition, setcondition] = useState('')
    const [foodtype, setfoodtype] = useState('')
    const [contact, setcontact] = useState('')
    const [location, setlocation] = useState('')
    


    const handleSubmit = (event) =>{
        console.log(image,donor,foodname,quality,quantity,condition,foodtype,contact,location);
        event.preventDefault();
        axios.post('http://localhost:8081/Add',{image,donor,foodname,quality,quantity,condition,foodtype,contact,location})
        .then(res =>{
            navigate('/Add');
            window.location.reload();
        }).catch(err => console.log(err));
    }
    const [data,setData]=useState([]);
    useEffect(()=>{
        axios.get('http://localhost:8081/Add')
        .then(res => {
            if (Array.isArray(res.data)) {
                setData(res.data);
            } else {
                setData([]);
            }
        })
        .catch(err => console.log(err));
    }, []);
    // Update the useEffect block in your client-side code

    useEffect(() => {
        axios.get('http://localhost:8081/fetchData')
            .then(res => {
                if (res.data.success) {
                    setData(res.data.data);
                } else {
                    console.log(res.data.error);
                    // Handle unauthorized access or other errors
                }
            })
            .catch(err => console.log(err));
    }, []);





    
    return (
      <div>
        <div className="home-container">
            <div className="menu">
                <button className="menu-btn" onClick={toggleMenu}>
                    <MenuIcon/>
                </button>
                {isMenuOpen && (
                    <div className="dropdown">
                    <button className="menu-item">Contact</button>
                    <button onClick={handleReload} className="menu-item">About Us</button>
                    </div>
                )}
            </div>
            <div className="tit">
                <h2 onClick={handleReload}><img src="/images/f3.png" alt=""/>FOOD NETWORK</h2>
            </div>
            <div className="buttons-container">
                <button onClick={handleLogout} className="sig-btn">Logout</button>
            </div>
        </div>
        

        <form action="" onSubmit={handleSubmit}>
            <div className="card-container">
                <div className="body-content">
                <div className="input-box">
                            <label>Image Details:</label>
                            <input onChange={e => setfoodname(e.target.value)}
                                type="file"
                                id="imageInput"
                                accept="image/*"
                            />
                        </div>
                        <div className="input-box">
                            <label>Food Name:</label>
                            <input type="text" placeholder="Enter food name" onChange={e => setimage(e.target.value)} />
                        </div>
                        <div className="input-box">
                            <label>Donor:</label>
                            <input type="text" placeholder="Enter donor" onChange={e => setdonor(e.target.value)}/>
                        </div>
                        <div className="input-box">
                            <label>Quality:</label>
                            <input type="text" placeholder="Enter quality" onChange={e => setquality(e.target.value)}/>
                        </div>
                        <div className="input-box">
                            <label>Quantity:</label>
                            <input type="text" placeholder="Enter quantity" onChange={e => setquantity(e.target.value)}/>
                        </div>
                        <div className="input-box">
                            <label>Condition:</label>
                            <input type="text" placeholder="Enter condition" onChange={e => setcondition(e.target.value)}/>
                        </div>
                        <div className="input-box">
                            <label>Food Type:</label>
                            <input type="text" placeholder="Enter food type" onChange={e => setfoodtype(e.target.value)}/>
                        </div>
                        <div className="input-box">
                            <label>Contact</label>
                            <input type="text" placeholder="Enter Contact" onChange={e => setcontact(e.target.value)}/>
                        </div>
                        <div className="input-box">
                            <label>location</label>
                            <input type="text" placeholder="Enter location" onChange={e => setlocation(e.target.value)}/>
                        </div>
                    
                </div>
                <button className="add-btn">Submit</button>
            </div>
        </form>
        <div className="body-content1">
            {data.map( (d,i)=>(

                <FoodCard2
                key={i}
                image={d.image}
                foodName={d.foodname}
                donorName={d.donor}
                contact={d.contact}
                location={d.location}
                quantity={d.quantity}
                quality={d.quality}
                foodType={d.foodtype}
                condition={d.condition}
                />

            ))}
            
            
            
            
        </div>



        <div className="footer">
            <h3>Copyright ⓒ {year}</h3>
        </div>
      </div>
    );
  }
  
  export default Add;
