import React, { useState } from "react";
import "./Foodcard.css"; // create a CSS file for styling
import FoodModal2 from "./FoodModal2.jsx";
const FoodCard = ({ image, foodName, donorName, contact, location, quantity, quality, foodType, condition }) => {
  const [isModalOpen, setIsModalOpen] = useState(false);


  const openModal = () => {
    setIsModalOpen(true);
  };
  const closeModal = () => {
    setIsModalOpen(false);
  };

  const convertBlobToDataURL = (blob) => {
    return new Promise((resolve, reject) => {
      const reader = new FileReader();
      reader.onloadend = () => resolve(reader.result);
      reader.onerror = reject;
      reader.readAsDataURL(blob);
    });
  };

  const displayImage = async () => {
    try {
      const dataURL = await convertBlobToDataURL(image);
      return dataURL;
    } catch (error) {
      console.error('Error converting blob to data URL', error);
    }
  };


  return (
    <div className="food-card" >
      <img src={displayImage()} alt={foodName} />
      <div className="card-details">
        <h3>{foodName}</h3>
        <p>Donor: {donorName}</p>
        <p>Contact: {contact}</p>
        <p>Location: {location}</p>
        <button className="car-btn" onClick={openModal}>View More</button>
        {isModalOpen && (
          <FoodModal2
            onClose={closeModal}
            {...{ image, foodName, donorName, contact, location, quantity, quality, foodType, condition }}
          />
        )}
      </div>
    </div>
  );
};

export default FoodCard;
