// FoodModal.js
// FoodModal.js
import React, { useState } from "react";
import "./FoodModal.css"; // create a CSS file for styling

const FoodModal2 = ({ image, foodName, donorName, contact, location, quantity, quality, foodType, condition, onClose }) => {
  const [isFullSize, setIsFullSize] = useState(false);
const toggleFullSize = () => {
  setIsFullSize(!isFullSize);
  const getImageUrl = (imageData) => {
    return `data:image/png;base64,${btoa(String.fromCharCode(...new Uint8Array(imageData)))}`;
  };
};
  return (
    <div className="food-modal">
      <div className={`modal-content ${isFullSize ? "full-size" : ""}`} onClick={(e) => e.stopPropagation()}>
        <img src={getImageUrl(image)} alt={foodName} onClick={toggleFullSize} />
        <h3>{foodName}</h3>
        <p>Donor: {donorName}</p>
        <p>Contact: {contact}</p>
        <p>Location: {location}</p>
        <p>Quantity: {quantity}</p>
        <p>Quality: {quality}</p>
        <p>Food Type: {foodType}</p>
        <p>Condition: {condition}</p>
        <button className="del-btn">Delete</button>
        <button className="cls-btn" onClick={onClose}>Close</button>
      </div>
    </div>
  );
};

export default FoodModal2;
