import React from "react";
import { useState,useEffect } from "react";
import "./Home.css";
import MenuIcon from '@mui/icons-material/Menu';


function Home() {
  const [isMenuOpen, setIsMenuOpen] = useState(false);

  const toggleMenu = () => {
    setIsMenuOpen(!isMenuOpen);
  };
  const closeMenu = () => {
    setIsMenuOpen(false);
  };

  useEffect(() => {
    document.addEventListener("click", closeMenu);
    return () => {
      document.removeEventListener("click", closeMenu);
    };
  }, []);

  const handleButtonClick = (e) => {
    e.stopPropagation(); // Prevents the document click event from being triggered
  };
    
  return (
    <div className="home-container">
      <div className="menu">
        <button className="menu-btn" onClick={toggleMenu}>
            <MenuIcon/>
        </button>
        {isMenuOpen && (
          <div className="dropdown" onClick={handleButtonClick}>
            <button className="menu-item">Contact Us</button>
            <button className="menu-item">About Us</button>
          </div>
        )}
      </div>
      <div className="buttons-container">
        <button className="log-btn">Login</button>
        <button className="sig-btn">Sign up</button>
      </div>
    </div>
  );
}

export default Home;
