// FoodModal.js
// FoodModal.js

import React from "react";
import "./FoodModal.css"; // create a CSS file for styling
const [isFullSize, setIsFullSize] = useState(false);
const toggleFullSize = () => {
  setIsFullSize(!isFullSize);
};
const FoodModal = ({ image, foodName, donorName, contact, location, quantity, quality, foodType, condition, onClose }) => {
  return (
    <div className="food-modal">
      <div className={`modal-content ${isFullSize ? "full-size" : ""}`} onClick={(e) => e.stopPropagation()}>
        <img src={image} alt={foodName} onClick={toggleFullSize} />
        <h3>{foodName}</h3>
        <p>Donor: {donorName}</p>
        <p>Contact: {contact}</p>
        <p>Location: {location}</p>
        <p>Quantity: {quantity}</p>
        <p>Quality: {quality}</p>
        <p>Food Type: {foodType}</p>
        <p>Condition: {condition}</p>
        <button onClick={onClose}>Close</button>
      </div>
    </div>
  );
};

export default FoodModal;
